class MobileController extends \Home\Controller\BaseController {
	protected function createAPIClient() {
		$settings = createService('System.SettingService')->get('storage', array());
		return new CloudAPI(array(
			'accessKey' => empty($settings['cloud_access_key']) ? '' : $settings['cloud_access_key'],
			'secretKey' => empty($settings['cloud_secret_key']) ? '' : $settings['cloud_secret_key'],
			'apiUrl'    => empty($settings['cloud_api_server']) ? '' : $settings['cloud_api_server'],
		));
	}

	public function indexAction(Request $request) {
		$mobile = $this->setting('mobile', array());

		if (empty($mobile['enabled'])) {
			return $this->createMessageResponse('info', '客户端尚未开启！');
		}

		//   $result = $this->createAPIClient()->get('/me');

		return $this->render('Mobile:index', array(
			'host'       => $request->getHttpHost(),
			'mobileCode' => ((array_key_exists("mobileCode", $result) && !empty($result["mobileCode"])) ? $result["mobileCode"] : "redcloud")
		));
	}

	public function downloadQrcodeAction() {
		$agent = strtolower($_SERVER ['HTTP_USER_AGENT']);
		if (strpos($agent, 'iphone') || strpos($agent, 'ipad') || $_REQUEST['ios']) {
			if (!C("SUPPORT_IOS_SCAN_PIC")) {
				die("暂不支持");
			}
		}
		if (strpos($agent, 'android')) {
			if (!C("SUPPORT_ANDROID_SCAN_PIC")) {
				die("暂不支持");
			}
		}

		$url    = C('SITE_URL') . '/app_down/'.time().'/';

		$qrCode = new QrCode();
		$qrCode->setText($url);
		$qrCode->setSize(150);
		$qrCode->setPadding(10);
		$img = $qrCode->get('png');

		$headers = array('Content-Type'        => 'image/png',
		                 'Content-Disposition' => 'inline; filename="image.png"');
		$rsp     = new Response($img, 200, $headers);
		$rsp->send();
		//return new Response($img, 200, $headers);
	}
        
        
        #qzw 2015-10-27
        public function urlAddTm($url) {
                $url = rtrim($url, '?');
                $url .= !preg_match('/\?/', $url) ? "?" : '&';
                $url .= time();
                return $url;
        }


	public function getDownloadUrlAction() {
		$url =  createService('Mobile.Version')->getCurrentDownloadByType(getDeviceType());
                if(isWeiXin()){
                    $url = U('Home/Mobile/default');
                }
		header("location:{$url}");
	}

        public function defaultAction(){
            return $this->render('Mobile:default');
        }

