<?phpnamespace Think;class SqlProcess{    static function isSelectSql($sql){        $sql = strtolower(trim($sql));        return strpos(trim($sql),"select") == 0 || strpos(trim($sql),"show") == 0;    }    static function isUpdateSql($sql){        $sql = strtolower(trim($sql));        return strpos(trim($sql),"update") == 0 || strpos(trim($sql),"alter") == 0;    }    static function isInsertSql($sql){        $sql = strtolower(trim($sql));        return strpos(trim($sql),"insert") == 0 || strpos(trim($sql),"create") == 0;    }    static function isDeleteSql($sql){        $sql = strtolower(trim($sql));        return strpos(trim($sql),"delete") == 0 || strpos(trim($sql),"drop") == 0;    }    static function addWhere($sql, $whereStr){        return trim($sql)." where ".trim($whereStr);    }    static function addInsertItem($sql, $items){        return $sql;    }}